#include <iostream>

using namespace std;

int main () {
    int a , ans{0};
    cin >> a;
    for (int i{0} ; i<a ; i++) {
        int p , q;
        cin >> p >> q;
        if (q - p >= 2) {
            ans ++;
        }
    }
    cout << ans;
    return 0;
}

#include <iostream>

using namespace std;

int main () {
    int T;
    cin >> T;
    while (T--) {
        int a , b , c;
        cin >> a >> b >> c;
        if (a + b == c) {
            cout << "Yes" << endl;
        }
        else if (b + c == a) {
            cout << "Yes" << endl;
        }
        else if (c + a == b) {
            cout << "Yes" << endl;
        }
        else {
            cout << "No" << endl;
        }
    }

    return 0;
}

