class Solution {
public:
    bool arrayStringsAreEqual(vector<string>& word1, vector<string>& word2) {
        string words1 = "";
        string words2 = "";
        for (const auto c : word1) {
            words1 += c;
        }
        for (const auto c : word2) {
            words2 += c;
        }
        if (words1==words2) {
            return true;
        }
        return false;
    }
};

