class Solution {
    public boolean arrayStringsAreEqual(String[] word1, String[] word2) {
        StringBuilder words1 = new StringBuilder();
        StringBuilder words2 = new StringBuilder();

        for (String w : word1) {
            words1.append(w);
        }
        for (String w : word2) {
            words2.append(w);
        }

        return words1.toString().equals(words2.toString());
    }
}

