#include <iostream>

using namespace std;

int main () {
    ios_base::sync_with_stdio(false);
    cin.tie(NULL);

    int T,ans{0};
    cin >> T;

    while (T--) {
        int a,b,c;
        cin >> a >> b >> c;
        if (a+b+c >= 2) {
            ans++;
        }
    }

    cout << ans;
    return 0;
}

solutionJava:
import java.util.Scanner;

public class c231A {
    public static void main (String [] args) {
        Scanner sc = new Scanner(System.in);
        int T = sc.nextInt();
        int ans = 0;
        while (T>0) {
            int a,b,c;
            a = sc.nextInt();
            b = sc.nextInt();
            c = sc.nextInt();
            if (a + b + c >= 2) {
                ans++;
            }
            T--;            
        }
        System.out.println(ans);
        sc.close();
    }
}
